/**
*  
* (n+k)kO(k)>O(n*log(n))
*  SSiSLSiT(Si)
*  LLLiLiT(Li)T(Li)1
**/
func countSort(sortArray:inout Array<Int>, maxValue:Int) -> Array<Int>{
let count = sortArray.count;
var retArray = Array<Int>();//
if count > 0 {
var countArray = Array<Int>();//
for _ in 0 ... maxValue {//
countArray.append(0);
}
for _ in 0 ..< count {
retArray.append(0);
}

//
for i in 0 ..< count {//i
countArray[sortArray[i]] = countArray[sortArray[i]] + 1;
}
for i in 1 ... maxValue {//i
countArray[i] = countArray[i] + countArray[i-1];
}

//sortArrayiretArraycountArraycountArray
for i in (0 ..< count).reversed() {
let value = sortArray[i];
let countValue = countArray[value];
retArray[countValue - 1] = value;
countArray[value] = countArray[value] - 1;
}
}
return retArray;
}
```

