*  
*  key
*  (j)key(i)i
*  keyi
*  i
**/
func quickSort<T:Comparable>(array:inout Array<T>, start:Int, end:Int){
if end <= start {
return;
}
let key = array[start];//key - 
var i = start;
var j = i + 1;
while j <= end {//keykey
let aj = array[j];
if key > aj {
//
let tempValue = array[i+1];
array[i+1] = aj;
array[j] = tempValue;
//
i += 1;
}
j += 1;//
}
//ikeykeyi
let ai = array[i];
array[i] = key;
array[start] = ai;

print("sortingkey\(key)  values: \(array[start...end])");

//i
quickSort(array: &array, start: start, end: i - 1)
quickSort(array: &array, start: i+1, end: end);
}
