/**
*  
* 
* , 
* LSDLeast significant digitalMSDMost significant digitalLSDMSD
*  - 
**/


/**
*
*  @brief  - LSD
*  @param  sortArray 
*  @param  loop    
*  @param  step    
*
**/
func radixSort(sortArray:inout Array<Int>, loop:Int,step:Int){

    let length = sortArray.count;//

    /**
    * 
    **/
    var msb = 1;//
    var minimum = 1;//
    for _ in 0 ..< loop * step {
        msb = msb * 10;
    }
    for _ in 0 ..< step {
        minimum = minimum * 10;
    }

    /**
    *  -   
    */
    var countArray = Array<Int>();
    var buckets = Array<Int>()
    for _ in 0 ... minimum {
        countArray.append(0);
    }
    for _ in 0 ..< length{
        buckets.append(0);
    }
    //
    for i in 0 ..< length {//i
        let sortValue = sortArray[i] / msb % minimum;
        countArray[sortValue] = countArray[sortValue] + 1;
    }
    for i in 1 ... minimum {//i
        countArray[i] = countArray[i] + countArray[i - 1];
    }
    //sortArrayiretArraycountArraycountArray
    for i in (0 ..< length).reversed() {
        let value = sortArray[i] / msb % minimum;
        let countValue = countArray[value];
        buckets[countValue - 1] = sortArray[i];
        countArray[value] = countArray[value] - 1;
    }

    //
    for i in 0 ..< length {
        sortArray[i] = buckets[i];
    }
}
